mapper.delete:
delete from mars_ff 
where mars_ff.user1_idx=#{user1_idx} and mars_ff.user2_idx=#{user2_idx}
or mars_ff.user1_idx=#{user2_idx} and mars_ff.user2_idx=#{user1_idx}

mapper.select:
select mypage.name, mypage.member_idx, mypage.profile_img
from mypage
inner join mars_ff on mypage.member_idx=mars_ff.user1_idx and mars_ff.user2_idx=#{member_idx}

select mypage.name, mypage.member_idx, mypage.profile_img
from mypage
inner join mars_ff on mypage.member_idx=mars_ff.user1_idx and mars_ff.user2_idx=#{member_idx}
where mars_ff.user1_idx=#{other_idx}
